function menuToggle(i){var t=document.getElementById("menu");switch(i){case"show":t.style.opacity=1,t.style.color="rgb(96, 96, 96)",t.style.visibility="visible",t.style.transition="visibility 0s, opacity 0.3s";break;case"hide":t.style.opacity=0,t.style.color="black",t.style.visibility="hidden",t.style.transition="visibility 0.3s, opacity 0.3s"}}